def log_call(func):
    def wrapper(*args, **kwargs):
        print("Calling", func.__name__)
        return func(*args, **kwargs)
    return wrapper

@log_call
def greet(name):
    print(f"Hello {name}")

def countdown(n):
    while n > 0:
        yield n
        n -= 1

for num in countdown(3):
    print(num)

@State private var isFlipped = false

Button("Flip") {
    isFlipped.toggle()
}

